import React from 'react';
import { Link } from 'react-router-dom';

class VideoCard extends React.Component {
    constructor(props) {
        super(props);
        this.toggleHover = this.toggleHover.bind(this);
        this.state = {
          isHovering: false,
        };
      }
    playVideo = () => {
        // You can use the play method as normal on your video ref
             if (this.refs.vidRef.paused || this.refs.vidRef.ended) this.refs.vidRef.play();
            else this.refs.vidRef.pause();
      };

    toggleHover = () => {
        this.setState({
            isHovering: !this.state.isHovering,
          });
    }

    // pauseVideo = () => {
    //     // Pause as well
    //     this.refs.vidRef.pause();
    //   };
    //   playpause.addEventListener('click', function(e) {
    //     if (video.paused || video.ended) video.play();
    //     else video.pause();
    //  });
    render() {
    return ( <>
        <div className="w3-container w3-light-gray" style={{padding:0,fontSize: '1.2em'}}>
        <div className="w3-margin-top w3-margin-left">
            <Link to="/dashboard"><i className="material-icons">arrow_back</i></Link>
        </div>
        <div className="w3-container w3-center" style={{
                  paddingLeft: '1em', paddingRight: '1em', margin:'0  1em 1em 1em'
                }}>
          <div className="w3-cell">
            <img src="https://www.w3schools.com/w3css/img_avatar3.png" alt="Avatar" className="w3-circle" style={{ width: '60px' }} />
          </div>
          <div className=" w3-container w3-cell ">
            <p className="" style={{margin:'0.8em 1em 1em 1em'}}>
                <b>Harikrishna's Creativity</b>
            </p>
          </div>
        </div>
        </div>
        <div className="w3-panel w3-display-container" onMouseEnter={this.toggleHover}>
        {this.state.isHovering && <button className="w3-display-middle" onClick={this.playVideo}>play/pause</button>}
            {/* <video onMouseOver={this.toggleHover} controls disablePictureInPicture  controlsList="nodownload" width="100%" src="https://clips.vorwaerts-gmbh.de/VfE_html5.mp4" type="video/mp4" ></video> */}
            <video
              width="100%"
              ref="vidRef"
              src="https://clips.vorwaerts-gmbh.de/VfE_html5.mp4"
              type="video/mp4"
            >
            </video>
          </div>
            {/* </div> */}
         </>);
}
}

export default VideoCard;
