import { useEffect } from 'react';

const ScrollToTop = ({ children, props, location: { pathname } }) => {
  if (this.props.location.pathname !== prevProps.location.pathname) {
    window.scrollTo(0, 0);
  }
  //   useEffect(() => {
  //     window.scrollTo(0, 0);
  //   }, [pathname]);
  return children || null;
};

export default ScrollToTop;
