import React, { useCallback,useEffect, useState } from 'react';
import { animated, useTransition } from 'react-spring';

import { fetchData } from './fetchCall.js';
// import { Spring } from "react-spring/renderprops";
import "./index.css";
import  { Card }  from "./Card.jsx";
const axios = require('axios');
const access_token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjVkNTExOGYzZmJlNjI5MzE0OTc0Y2EwYiIsImlhdCI6MTU2NTU5NjAwOX0.kwgnPc0jcqGL3RBw8MOmI2eZTaRnuCqy1NmIAPsXZAY';

// import { async } from 'q';

const URL = 'https://dev.mai.family/api/v1/child/5d5119befbe629314974ca0d/assessment/5ce28e02990bef418d6aceb0';
const postUrl = 'https://dev.mai.family/api/v1/child/5d5119befbe629314974ca0d/assessment'
let assessmentId = '5ce28e02990bef418d6aceb0';

const CardsList = () => {
    const [index, setIndex] = useState(0);
    const [status,setStatus] = useState('loading')
    const [PostData, setPostData] = useState([{ data: [], status: "INIT" }]);
    const [progress, setProgress] = useState(0);

    const sendResponse = async(URL, parameters) => {
      let result = {data:{}}
      await axios({
        method: 'post',
        url: URL,
        data: parameters,
        headers: {
          'Content-Type': 'application/json',
          'x-access-token': access_token,
         }
      })
      .then(
          response => {
              result.data = response
              // result.status = response.data.data.progress.toFixed(0);
              setProgress((response.data.data.progress).toFixed(0));
              console.log('Progress:',(response.data.data.progress).toFixed(0));
          }
      ).catch(error => {
          console.log('error :', error);
          result.status="ERROR";
          return result;
      })  
    }

    const handleClick = useCallback(async(res, questionId) => {
      let responseData = {
          "assessmentId": "5ce28e02990bef418d6aceb0",
          "questionId": questionId,
          "response": res
      }
      console.log('user_response',{'assessmentId ': assessmentId,
                  'questionId ':questionId,
                  'user_response ': res,});
      await sendResponse(postUrl, responseData)
      // .then(() => {console.log(progress);
      await console.log(PostData);
      await setIndex(state => {
        console.log(progress)
        console.log(state)
        return (state + 1)
      })
      // })
    },[]);

    const getData = async() => {
      const response = await fetchData(URL);
      await setPostData(response.data.data.data.questions)
      await setStatus(response.status)
      }

     useEffect(() => {
        getData();
        // console.log(progress);
      },[])

    let pages = PostData.map(card => ({ style }) => (
      <animated.div style={{ ...style }}>
          <Card fun={handleClick} data = { card }/>
      </animated.div>
       ));

    // console.log(progress);
    const transitions = useTransition((index), p => p, {
      from: { opacity: 0 , marginTop: '100%', padding: '1em'},
      enter: { opacity: 1 , marginTop: '20%', padding: '1em'},
      leave: { opacity: 0 , marginTop: '-100%', padding: '1em'},
    })

    const next_transition = useTransition((index), p => p, {
      from: { opacity: 0 , marginTop: '100%', padding: '1em' },
      enter: { opacity: 0.3 , marginTop: '10%', padding: '1em' },
      leave: { opacity: 0 , marginTop: '-100%', padding: '1em' },
    })

    return (
      <div className="App">
      {(status==='SUCCESS') ?
      <div className="simple-trans-main">

        {transitions.map(({ item, props, key }) => {
          const Page = pages[item];
          return (<div key={key}><Page style={props} /></div>)
         })}
        {next_transition.map(({ item, props, key }) => {
          const Page = pages[item+1];
          return (<div key={key}><Page style={props} /></div>)
        })}
        {console.log(progress)}
      </div> : <div key={0} style={{marginLeft: '200%', marginTop: '200%',fontSize: '2em'}}>
        Loading...
        </div>
      }
    </div>
     ); 
}

export default CardsList;
