import React, { useCallback,useEffect, useState } from 'react';
import { animated, useTransition } from 'react-spring';

import fetchData from './fetchCall.js'
// import { Spring } from "react-spring/renderprops";
import "./index.css";
import  { Card }  from "./Card.jsx";


const URL = 'https://dev.mai.family/api/v1/child/5d5119befbe629314974ca0d/assessment/5ce28e02990bef418d6aceb0';

const CardsList = () => {
    const [index, set] = useState(0);
    const [status,setStatus] = useState('loading')
    const [PostData, setPostData] = useState([{ data: [], status: "INIT" }]);
    const handleClick = useCallback(() => set(state => (state + 1)),[]);

    const getData = async() => {
      const questions = await fetchData(URL);
      setPostData(questions)
      setStatus("SUCCESS")
      }
      useEffect(() => {
        getData();
      },[])
    let pages;
    const transitions = useTransition((index), p => p, {
      from: { opacity: 0 , marginTop: '100%', padding: '1em'},
      enter: { opacity: 1 , marginTop: '30%', padding: '1em'},
      leave: { opacity: 0 , marginTop: '-100%', padding: '1em'},
    })

    const next_transition = useTransition((index), p => p, {
      from: { opacity: 0 , marginTop: '100%', padding: '1em' },
      enter: { opacity: 0.3 , marginTop: '10%', padding: '1em' },
      leave: { opacity: 0 , marginTop: '-100%', padding: '1em' },
    })

    return ( 
        <div className="App">
      {(status==='SUCCESS') ?
      <div className="simple-trans-main">

        {transitions.map(({ item, props, key }) => {
         pages = PostData.map(card => ({ style }) => (
              <animated.div style={{ ...style }}>
                  <Card fun={handleClick} data = { card }/>
              </animated.div>
               ));
          const Page = pages[item];
          return (<div key={key}><Page style={props} /></div>)
         })}

        {next_transition.map(({ item, props, key }) => {
          const Page = pages[item+1];
          return (<div key={key}><Page style={props} /></div>)
        })}

      </div> : <div key={0} style={{marginLeft: '200%',
    marginTop: '200%',
    fontSize: '2em'}}>Loading...</div>}
    </div>
     );
}

export default CardsList;

