import React from 'react';
import { Link } from 'react-router-dom';

class VideoCard extends React.Component {
    playVideo = () => {
        // You can use the play method as normal on your video ref
        this.refs.vidRef.play();
      };

    pauseVideo = () => {
        // Pause as well
        this.refs.vidRef.pause();
      };
    //   playpause.addEventListener('click', function(e) {
    //     if (video.paused || video.ended) video.play();
    //     else video.pause();
    //  });
    render() {
    return ( <>
        <div className="w3-container w3-light-gray" style={{padding:0,fontSize: '1.2em'}}>
        <div className="w3-margin-top w3-margin-left">
            <Link to="/dashboard"><i className="material-icons">arrow_back</i></Link>
        </div>
        <div className="w3-container w3-center" style={{
                  paddingLeft: '1em', paddingRight: '1em', margin:'0  1em 1em 1em'
                }}>
          <div className="w3-cell">
            <img src="https://www.w3schools.com/w3css/img_avatar3.png" alt="Avatar" className="w3-circle" style={{ width: '60px' }} />
          </div>
          <div className=" w3-container w3-cell ">
            <p className="" style={{margin:'0.8em 1em 1em 1em'}}>
                <b>Harikrishna's Creativity</b>
            </p>
          </div>
        </div>
        </div>
        <div className="w3-panel">
            {/* <video
              width="100%"
              ref="vidRef"
              src="https://s3.amazonaws.com/codecademy-content/courses/React/react_video-cute.mp4"
              type="video/mp4"
            />

            <div>
              <button onClick={this.playVideo}>
                Play!
              </button>
              <button onClick={this.pauseVideo}>
                Pause!
              </button>
            </div>
          </div> */}
          <video  disablePictureInPicture  controlslist="nodownload" width="100%" src="https://clips.vorwaerts-gmbh.de/VfE_html5.mp4" type="video/mp4" ></video>
          <ul id="video-controls" class="controls">
   <li><button onClick={() => {
                      if (video.paused || video.ended) video.play();
                        else video.pause();
   }} id="playpause" type="button">Play/Pause</button></li>
   <li><button id="stop" type="button">Stop</button></li>
   <li class="progress">
      <progress id="progress" value="0" min="0">
         <span id="progress-bar"></span>
      </progress>
   </li>
   <li><button id="mute" type="button">Mute/Unmute</button></li>
   <li><button id="volinc" type="button">Vol+</button></li>
   <li><button id="voldec" type="button">Vol-</button></li>
   <li><button id="fs" type="button">Fullscreen</button></li>
</ul>
            </div>
         </>);
}
}

export default VideoCard;
