export default function App () {

    const [cards, setCards] = useState(PostsData);
    const [fade, setFade] = useState(false);
    const [items, setItems] = useState(1);

    const onAnswerHandle = () => {
        let newData = cards.filter(card => cards[0] !== card);
        setCards(newData);
        setFade(true);
    };

        return ( <div  className = {`App ${fade ?"fade" : ''}`} >
        {/* <TransitionGroup component="div"> */}
            {
                cards.filter(post => cards.indexOf(post) < noOfCards)
                    .map((post, i) => (
                        // <CSSTransition key={i} timeout={500} classNames="messageout">
                        <MediaCard index = { i } data = { post } onAnswerHandle = { onAnswerHandle }/>
                        /* </CSSTransition>  */
                ))
            }
        {/* </TransitionGroup> */}
        <button
          type="button"
          onClick={() => {
            setItems([
                ...items,
                items.length > 0
                  ? items[items.length - 1] + 1
                  : 1,
              ],
            );
          }}
        >
          + Add item
        </button>
        <button
          type="button"
          onClick={() => {
            this.setState(state => ({
              items: state.items.slice(0, -1),
            }));
          }}
        >
          - Remove item
        </button>
        <TransitionGroup>
          {items.map(i => (
            <CSSTransition key={i} timeout={timeout} classNames="fade">
              <div>Item {i}</div>
            </CSSTransition>
          ))}
        </TransitionGroup>
             </div> 
        );
