  appearActive: "appearActive"
};

let newStyle = () => {
  if (data < 0.3) {
    data = 1;
  }
  data = (data - 0.2).toFixed(1);
  let divStyle = {
    opacity: data
  };
  return divStyle;
};

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: PostsData,
      visibility: false
    };
    this.onAnswerHandle = this.onAnswerHandle.bind(this);
  }

  componentDidMount() {
    this.onAnswerHandle();
  }

  onAnswerHandle = () => {
    // eslint-disable-next-line
    let newData = this.state.posts.filter(post => this.state.posts[0] !== post);
    this.setState(() => {
      return {
        posts: newData
      };
    });
  };

  render() {
    return (
      <div className="App">
        <CSSTransitionGroup
          transitionName="example"
          transitionAppear={true}
          transitionAppearTimeout={1000}
          transitionEnter={false}
          transitionLeave={false}
        >
          <div key="1">
            {this.state.posts
              .filter(post => this.state.posts.indexOf(post) < 5)
              .map((post, i) => (
                <div key={i} style={i === 0 ? transitionName : newStyle()}>
                  <MediaCard
                    key={i}
                    index={i}
                    data={post}
                    onAnswerHandle={this.onAnswerHandle}
                  />{" "}
                </div>
              ))}
          </div>
        </CSSTransitionGroup>
